import React, { useState } from 'react';
import '../styles/ChatFooter.css';
import axios from "axios";

const ChatFooter = ({ addMessage }) => {
  const [input, setInput] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (input.trim()) {
      if (typeof addMessage === 'function') { // Comprobar si addMessage es una función
        addMessage({ sender: 'user', text: input });

        try {
          const response = await axios.post("/api/chat", {
            message: input,
          });

          addMessage({ sender: "bot", text: response.data.response });
        } catch (error) {
          console.error("Error al comunicarse con el backend:", error);
          addMessage({ sender: "bot", text: "Error al comunicarse con el asistente." });
        }

        setInput('');
      } else {
        console.error('addMessage no es una función');
      }
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      addMessage({ text: `Imagen adjunta: ${file.name}`, isUser: true });
    }
  };

  return (
    <div className="chat-footer">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Escribe tu mensaje..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button type="submit">Enviar</button>
        <input type="file" id="imageUpload" onChange={handleImageUpload} />
        <label htmlFor="imageUpload" className="image-upload">
          📎
        </label>
      </form>
    </div>
  );
};

export default ChatFooter;
