// src/components/ChatFooter.js
import React, { useState } from 'react';
import '../styles/ChatFooter.css';

const ChatFooter = ({ addMessage }) => {
  const [input, setInput] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (input.trim()) {
      addMessage({ sender: 'user', text: input });
      setInput('');
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      addMessage({ text: `Imagen adjunta: ${file.name}`, isUser: true });
    }
  };

  return (
    <div className="chat-footer">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Escribe tu mensaje..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button type="submit">Enviar</button>
        <input type="file" id="imageUpload" onChange={handleImageUpload} />
        <label htmlFor="imageUpload" className="image-upload">
          ðŸ“Ž
        </label>
      </form>
    </div>
  );
};

export default ChatFooter;
